// Rough mapping from lat/lng to an SVG viewBox for Saudi region
const BOUNDS = {
  minLat: 16.0,
  maxLat: 32.5,
  minLng: 34.0,
  maxLng: 55.5,
};

function projectToViewBox(lat: number, lng: number) {
  // viewBox 0 0 1000 824 (matching the accurate SVG map)
  const x = ((lng - BOUNDS.minLng) / (BOUNDS.maxLng - BOUNDS.minLng)) * 1000;
  const y = ((BOUNDS.maxLat - lat) / (BOUNDS.maxLat - BOUNDS.minLat)) * 824;
  return { x, y };
}

