function depositGlowColor(d: MineralDeposit) {
  const c = d.commodities;
  if (c.includes("gold")) return "#D4AF37";
  if (c.includes("copper")) return "#F59E0B";
  if (c.includes("zinc")) return "#6B7280";
  if (c.includes("iron")) return "#EF4444";
  if (c.includes("phosphate")) return "#16A34A";
  return "#22C55E";
}

export interface SaudiSilhouetteProps {
  className?: string;
  maxPoints?: number;
}

export function SaudiSilhouette({ className, maxPoints = 18 }: SaudiSilhouetteProps) {
  const points = useMemo(() => {
    return mineralDeposits
      .slice(0, maxPoints)
      .map((d) => {
        const { x, y } = projectToViewBox(d.location.latitude, d.location.longitude);
        return { id: d.id, name: d.name, x, y, color: depositGlowColor(d) };
      });
  }, [maxPoints]);

  const [selectedId, setSelectedId] = useState<string | null>(points[0]?.id ?? null);
  const selected = useMemo(() => mineralDeposits.find((d) => d.id === selectedId), [selectedId]);

  return (
    <div className={className}>
      <svg viewBox="0 0 1000 824" preserveAspectRatio="xMidYMid meet" className="w-full h-auto">
        <defs>
          <filter id="softGlow" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur stdDeviation="6" result="coloredBlur" />
            <feMerge>
              <feMergeNode in="coloredBlur" />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
          <linearGradient id="silhouetteFill" x1="0" y1="0" x2="1" y2="1">
            <stop offset="0%" stopColor="rgba(0,108,53,0.45)" />
            <stop offset="100%" stopColor="rgba(212,175,55,0.15)" />
          </linearGradient>
        </defs>

        {/* Accurate Saudi Arabia map with all regions */}
        <g id="saudi-regions">
          {SAUDI_REGION_PATHS.map((r) => (
            <path
              key={r.id}
              d={r.d}
              fill="url(#silhouetteFill)"
              stroke="rgba(212,175,55,0.35)"
              strokeWidth="1.1"
              strokeLinejoin="round"
              strokeLinecap="round"
              vectorEffect="non-scaling-stroke"
              shapeRendering="geometricPrecision"
            />
          ))}
        </g>

        {/* Deposit points */}
        {points.map((p, idx) => (
          <g key={p.id}>
            <motion.circle
              cx={p.x}
              cy={p.y}
              r={10}
              fill={p.color}
              opacity={0.22}
              filter="url(#softGlow)"
              animate={{ opacity: [0.18, 0.32, 0.18], r: [9, 12, 9] }}
              transition={{ duration: 2.2, repeat: Infinity, delay: idx * 0.08, ease: "easeInOut" }}
              whileHover={{ scale: 1.08, opacity: 0.35 }}
              onClick={() => setSelectedId(p.id)}
              role="button"
              aria-label={`Select ${p.name}`}
              className="cursor-pointer"
            />
            <motion.circle
              cx={p.x}
              cy={p.y}
              r={3.4}
              fill="rgba(255,255,255,0.95)"
              animate={{ scale: [1, 1.15, 1] }}
              transition={{ duration: 1.8, repeat: Infinity, delay: idx * 0.06, ease: "easeInOut" }}
              onClick={() => setSelectedId(p.id)}
              role="button"
              aria-label={`Select ${p.name}`}
              className="cursor-pointer"
            />
          </g>
        ))}
      </svg>

      {selected ? (
        <div className="mt-4 rounded-2xl border border-white/10 bg-white/5 p-4 text-sm text-white/90 shadow-sm backdrop-blur">
          <div className="flex items-start justify-between gap-3">
            <div>
              <div className="font-semibold text-white">{selected.name}</div>
              <div className="text-xs text-white/70">
                {selected.location.region} â€¢ {selected.commodities.join(", ")}
              </div>
            </div>
            <span className="text-[10px] px-2 py-1 rounded-full bg-saudi-gold-500/15 text-saudi-gold-200 border border-saudi-gold-500/25">
              {selected.status ?? "active"}
            </span>
          </div>
          {selected.description ? (
            <p className="mt-2 text-xs text-white/80 leading-snug line-clamp-3">{selected.description}</p>
          ) : null}
        </div>
      ) : null}
    </div>
  );
}


